<?phpnamespace Nemundo\Store\Com\Button;use Nemundo\Store\Parameter\StoreParameter;use Nemundo\Store\Site\Editor\HtmlTextStoreEditSite;use Nemundo\Store\Type\AbstractLargeTextStoreType;class HtmlStoreSiteButton extends AbstractStoreSiteButton{    /**     * @var AbstractLargeTextStoreType     */    public $store;    public function getContent()    {        $this->site = clone(HtmlTextStoreEditSite::$site);        $this->site->addParameter(new StoreParameter($this->store->storeId));        return parent::getContent();    }}