<?phpnamespace Nemundo\Store\Com\Button;use Nemundo\Store\Parameter\StoreParameter;use Nemundo\Store\Site\Editor\TextStoreEditorSite;use Nemundo\Store\Type\AbstractTextStoreType;class TextStoreSiteButton extends AbstractStoreSiteButton{    /**     * @var AbstractTextStoreType     */    public $store;    public function getContent()    {        $this->site = TextStoreEditorSite::$site;        $this->site->addParameter(new StoreParameter($this->store->storeId));        return parent::getContent();    }}