<?phpnamespace Nemundo\Store\Com\Form;use Nemundo\Admin\Com\Form\AbstractAdminForm;use Nemundo\Com\FormBuilder\Item\HiddenFormItem;use Nemundo\Core\Http\Request\Post\PostRequest;use Nemundo\Core\Http\Url\UrlRedirect;use Nemundo\Core\Http\Url\UrlReferer;use Nemundo\Html\Formatting\Small;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\Store\Type\AbstractStoreType;class AbstractStoreForm extends AbstractAdminForm  // BootstrapForm{    /**     * @var AbstractStoreType     */    public $store;    /**     * @var bool     */    public $urlRefererRedirect = false;    /**     * @var HiddenFormItem     */    private $hidden;    public function getContent()    {        $small = new Small($this);        $small->content = 'Id = ' . $this->store->storeId;        if ($this->urlRefererRedirect) {            $this->hidden = new HiddenFormItem($this);            $this->hidden->name = 'redirect';            if ($this->hidden->hasValue()) {                $this->hidden->value = $this->hidden->getValue();            } else {                $this->hidden->value = (new UrlReferer())->getUrl();            }        }        return parent::getContent();    }    protected function getStoreValue()    {        return null;    }    protected function onSubmit()    {        $this->store->setValue($this->getStoreValue());        if ($this->urlRefererRedirect) {            $url = (new PostRequest('redirect'))->getValue();            (new UrlRedirect())->redirect($url);        }    }}