<?phpnamespace Nemundo\Store\Com\Form;use Nemundo\Admin\Com\HtmlEditor\AdminHtmlEditor;class HtmlStoreForm extends AbstractStoreForm{    /**     * @var AdminHtmlEditor     */    private $text;    public function getContent()    {        $this->text = new AdminHtmlEditor($this);        $this->text->label = $this->store->storeLabel;        $this->text->value = $this->store->getValue();        return parent::getContent();    }    protected function getStoreValue()    {        $value = $this->text->getValue();        return $value;    }}