<?phpnamespace Nemundo\Store\Com\Form;use Nemundo\Admin\Com\ListBox\AdminLargeTextBox;use Nemundo\Store\Type\AbstractLargeTextStoreType;class LargeTextStoreForm extends AbstractStoreForm{    /**     * @var AbstractLargeTextStoreType     */    public $store;    /**     * @var AdminLargeTextBox     */    private $text;    public function getContent()    {        $this->text = new AdminLargeTextBox($this);        $this->text->label = $this->store->storeLabel;        $this->text->value = $this->store->getValue();        return parent::getContent();    }    protected function getStoreValue()    {        $value = $this->text->getValue();        return $value;    }}